const isMtrxLike = require('mtrx/src/isMtrxLike'),
      isSquare = require('mtrx/src/isSquare'),
      every = require('mtrx/src/every');

function isDiag(matrix) {
  return isMtrxLike(matrix) &&
    isSquare(matrix) &&
    every(matrix, (c, i, j) =>
          (i === j)
          ? true
          : (c === 0));
}

module.exports = isDiag;
